@import 'variables.scss';
@import 'mixings.scss';
html {
    box-sizing: border-box;
    color: #333;
    font-size: 100%;
}
*,*::before, *::after{
  box-sizing: inherit;
}
h1,h2,h3{
  margin-top: 0px;
}

body{
  //background-color:rgb(204, 31, 31);
  padding: 0;
  margin: 0;
  line-height: 1.3;
  font-family: 'Public Sans' sans-serif;
  font-size: $font-med;
  color: $grayishBlue;
  font-weight: 300;

  @include breakpoint-up(large){
    font-size: $font-med;
 

}
  
 
}
a,a:visited, a:hover{
  text-decoration: none;
}

//Spacing
.container{
  &--pall{
    padding-top: 2.25rem;
    padding-bottom: 2.25rem;
    padding-right: 1.5rem;
    padding-left: 1.5rem;
    
  }
  &--px{
    padding-left: 1.5rem;
    padding-right: 1.5rem;

  }
  &--py{
    padding-top: 2.25rem;
    padding-bottom: 2.25rem;
  }
  
  &--pt{
    padding-top: 2.25rem;
    
  }
  &--pb{
    padding-bottom: 2.25rem;

  }
  &--pr{
    padding-right: 1.5rem;

  }
  &--pl{
    padding-left: 1.5rem;

  }

  max-width: 69.375rem;
  margin: 0 auto;
}

//Flexboc Items

.flex{
  display: flex;
  

  &-jc-sb{
    justify-content: space-between;
  }
  &-jc-c{
    justify-content: center;
  }
  &-ai-c{
    align-items: center;;
  }

}
//Buttons

button,.button{
  padding: 0.875rem 2.1875rem;
  border: 0;
  border-radius: 50px;
  cursor: pointer;
  font-weight: 400;
  color: $White;
  background:linear-gradient(to right, $LimeGreen, $brightCyan) ;
  font-size: $font-sm;

  transition: opacity 300ms ease-in-out;
  display:inline-block;
  
  &:hover{
    opacity: 0.75;
  }
}
  




//Visibility
.hide-for-mobile{
  //Hide for tablet and mobile devices
  @include breakpoint-down(medium){
    display: none;
  }

}

.hide-for-desktop{
  //Hide for desktop
  @include breakpoint-up(large){
    display: none;
  }
}

